 "codeSnippet": "export default {
 async fetch(request, env, ctx) {
 try {
 userCode = env.UUID || userCode;
 proxyIP = env.PROXYIP || proxyIP;
 dnsResolver = env.DNS_RESOLVER || dnsResolver;

 const actualScamalyticsUsername = env.SCAMALYTICS_USERNAME || SCAMALYTICS_USERNAME;
 const actualScamalyticsApiKey = env.SCAMALYTICS_API_KEY || SCAMALYTICS_API_KEY;


 if (!isValidUserCode(userCode)) {
 throw new Error("Invalid user code");
 }

 const upgradeHeader = request.headers.get("Upgrade");
 const url = new URL(request.url);

 if (!upgradeHeader || upgradeHeader !== "websocket") {
		switch (url.pathname) {
			case '/':
 case `/${userCode}`:
				const hostName = request.headers.get('Host');
				return new Response(await getHtml(userCode, hostName), {
					headers: {
						"Content-Type": "text/html;charset=UTF-8",
					},
				});

			case '/api/ip':
				return new Response(JSON.stringify({ ip: request.headers.get('CF-Connecting-IP') }), {
 headers: {
 "Content-Type": "application/json",
 "Access-Control-Allow-Origin": "*",
 }
 });

			case '/scamalytics-lookup':
				const ipToLookup = url.searchParams.get('ip');
				if (!ipToLookup) {
					return new Response('Missing IP parameter', { status: 400 });
				}
				if (actualScamalyticsUsername === '<YOUR_SCAMALYTICS_USERNAME>' || actualScamalyticsApiKey === '<YOUR_SCAMALYTICS_API_KEY>' || actualScamalyticsUsername === '' || actualScamalyticsApiKey === '') {
					console.error('Scamalytics credentials not configured in Worker (env or hardcoded).');
					return new Response('Scamalytics API credentials not configured on server.', { status: 500 });
				}
				const scamalyticsUrl = `${SCAMALYTICS_API_BASE_URL}${actualScamalyticsUsername}/?key=${actualScamalyticsApiKey}&ip=${ipToLookup}`;
				try {
					const scamalyticsResponse = await fetch(scamalyticsUrl);
					const responseBody = await scamalyticsResponse.json();
					const headers = new Headers({
						'Content-Type': 'application/json',
						'Access-Control-Allow-Origin': '*',
						'Access-Control-Allow-Methods': 'GET, OPTIONS',
						'Access-Control-Allow-Headers': 'Content-Type',
					});
					return new Response(JSON.stringify(responseBody), {
						status: scamalyticsResponse.status,
						headers: headers,
					});
				} catch (apiError) {
					console.error('Error fetching from Scamalytics API:', apiError);
					return new Response(JSON.stringify({ error: 'Failed to fetch from Scamalytics API', details: apiError.message }), {
						status: 502,
						headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
					});
				}

			default:
				if (url.pathname.startsWith('/api/geoip/')) {
					const ip = url.pathname.split('/')[3];
					if (!ip) {
						return new Response('Missing IP parameter in path', { status: 400 });
					}
					const geoData = await fetchGeoIpWithFallback(ip);
					if (geoData) {
						return new Response(JSON.stringify(geoData), {
							headers: {
								'Content-Type': 'application/json',
								'Access-Control-Allow-Origin': '*',
							},
						});
					} else {
						return new Response(JSON.stringify({ error: 'Failed to fetch GeoIP data from all sources' }), {
							status: 502,
							headers: {
								'Content-Type': 'application/json',
								'Access-Control-Allow-Origin': '*',
							},
						});
					}
				}
				return new Response('Not found', { status: 404 });
		}
 } else {
 return await streamOverWSHandler(request);
 }
 } catch (err) {
 console.error("Fetch error:", err);
 return new Response(err.toString(), { status: 500 });
 }
 }",
 "description": "The `fetch` function is very large (over 80 lines of code) and handles multiple distinct responsibilities (serving HTML, API endpoints, WebSocket proxying) within a single `switch` statement. This leads to deep nesting (up to 4-5 levels in some branches) and makes the function hard to read, understand, and maintain. Consider extracting route handlers into separate, more focused functions.",
